var Users=require("../models/users.js");
function ClickHandler(){this.getClicks=function(a,b){Users.findOne({"github.id":a.user.github.id},{_id:!1}).exec(function(c,a){if(c)throw c;b.json(a.nbrClicks)})};this.addClick=function(a,b){Users.findOneAndUpdate({"github.id":a.user.github.id},{$inc:{"nbrClicks.clicks":1}}).exec(function(c,a){if(c)throw c;b.json(a.nbrClicks)})};this.resetClicks=function(a,b){Users.findOneAndUpdate({"github.id":a.user.github.id},{"nbrClicks.clicks":0}).exec(function(a,d){if(a)throw a;b.json(d.nbrClicks)})}}
module.exports=ClickHandler;
